import React from 'react';
import DialogItem from './DialogItem/DialogItem';
import Message from './Message/Message';
import s from './Dialogs.module.css';
import { addNewMessageActionCreator, updateNewMessageTextActionCreator } from "../../redux/dialogs_reducer";

const Dialogs = (props) => {
	let dialogsElements = props.state.dialogs.map(d => <DialogItem name={d.name} id={d.id} />)
	let messagesElements = props.state.messages.map(m => <Message message={m.message} />)

	let addNewMessage = () => {
		props.dispatch(addNewMessageActionCreator())
	}

	let onMessageChange = (e) => {
		let text = e.target.value;
		let action = (updateNewMessageTextActionCreator(text))
		props.dispatch(action)
	}
	return (
		<div className={s.dialogs}>
			<div className={s.dialogsItems}>
				{dialogsElements}
			</div>
			<div className={s.messages}>
				{messagesElements}
				<div className={s.message}>
					<div className={s.textarea__container}>
						<textarea onChange={onMessageChange} placeholder='Enter a message!' value={props.state.newMessageText} />
						<button onClick={addNewMessage}>Send Message</button>
					</div>
				</div>
			</div>
		</div>
	)
}

export default Dialogs;
