const ADD_NEW_MESSAGE = 'ADD-NEW-MESSAGE'
const UPDATE_NEW_MESSAGE_TEXT = 'UPDATE-NEW-MESSAGE-TEXT'

let initialState = {
	users: [
		{ id: 1, name: 'Alex', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'I\'m fine! Thanks! And you?', location: { city: 'Kyiv', country: 'Ukraine', } },
		{ id: 2, name: 'Andrey', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'Who is on duty today?', location: { city: 'Washington', country: 'USA', } },
		{ id: 3, name: 'Sherlock', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'The sun is shining. The weather is fine.', location: { city: 'London', country: 'England', } },
		{ id: 4, name: 'Sasha', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'dast is fantastish, yavol!', location: { city: 'Berlin', country: 'Germany', } },
	],
}

const usersReducer = (state = initialState, action) => {
	switch (action.type) {
		case FOLLOWED:
			return {
				...state,
				users: [...state.users]
			}
	}













	switch (action.type) {
		case ADD_NEW_MESSAGE:
			let body = state.newMessageText
			return {
				...state,
				newMessageText: '',
				messages: [...state.messages, { id: 7, message: body, }],
			}

		case UPDATE_NEW_MESSAGE_TEXT:
			return {
				...state,
				newMessageText: action.newText
			}

		default: return state
	}
}
export const addNewMessageActionCreator = () => {
	return { type: ADD_NEW_MESSAGE }
}

export const updateNewMessageTextActionCreator = (text) => {
	return {
		type: UPDATE_NEW_MESSAGE_TEXT, newText: text
	}
}

export default usersReducer;
