const FOLLOWED = 'FOLLOWED'
const UNFOLLOWED = 'UNFOLLOWED'

let initialState = {
	users: [
		{ id: 1, fullName: 'Alex', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'I\'m fine! Thanks! And you?', location: { city: 'Kyiv', country: 'Ukraine', } },
		{ id: 2, fullName: 'Andrey', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'Who is on duty today?', location: { city: 'Washington', country: 'USA', } },
		{ id: 3, fullName: 'Sherlock', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'The sun is shining. The weather is fine.', location: { city: 'London', country: 'England', } },
		{ id: 4, fullName: 'Sasha', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'dast is fantastish, yavol!', location: { city: 'Berlin', country: 'Germany', } },
	],
}

const usersReducer = (state = initialState, action) => {
	switch (action.type) {
		case FOLLOWED:
			return {
				...state,
				users: state.users.map(u => {
					if (u.id === action.userId) {
						return { ...u, followed: true }
					}
					return u
				})
			}
		case UNFOLLOWED:
			return {
				...state,
				users: state.users.map(u => {
					if (u.id === action.userId) {
						return { ...u, followed: false }
					}
					return u
				})
			}
		default: return state
	}
}

export const followActionCreator = (userId) => {
	return { type: FOLLOWED, userId }
}

export const unFollowActionCreator = (userId) => {
	return { type: UNFOLLOWED, userId }
}

export const setUserActionCreator = () => {
	return {}
}

export default usersReducer;
