import React from 'react';
import { addNewMessageActionCreator, updateNewMessageTextActionCreator } from "../../redux/dialogs_reducer";
import Dialogs from './Dialogs';

const DialogsContainer = (props) => {
	let state = props.store.getState()
	let dialogsPage = state.dialogsPage

	let addNewMessage = () => {
		props.store.dispatch(addNewMessageActionCreator())
	}

	let messageChange = (text) => {
		let action = (updateNewMessageTextActionCreator(text))
		props.store.dispatch(action)
	}
	return (
		<Dialogs addNewMessage={addNewMessage} messageChange={messageChange} state={dialogsPage} />
	)
}

export default DialogsContainer;
