import React from 'react';
import DialogItem from './DialogItem/DialogItem';
import Message from './Message/Message';
import s from './Dialogs.module.css';

const Dialogs = (props) => {
	let dialogsElements = props.state.dialogs.map(d => <DialogItem name={d.name} key={d.id} id={d.id} />)
	let messagesElements = props.state.messages.map(m => <Message message={m.message} key={m.id} />)

	let onAddNewMessage = () => {
		props.addNewMessage()
	}

	let onMessageChange = (e) => {
		let text = e.target.value;
		props.messageChange(text)
	}
	return (
		<div className={s.dialogs}>
			<div className={s.dialogsItems}>
				{dialogsElements}
			</div>
			<div className={s.messages}>
				{messagesElements}
				<div className={s.message}>
					<div className={s.textarea__container}>
						<textarea onChange={onMessageChange} placeholder='Enter a message!' value={props.state.newMessageText} />
						<button onClick={onAddNewMessage}>Send Message</button>
					</div>
				</div>
			</div>
		</div>
	)
}

export default Dialogs;
