import React from 'react';
import s from './Users.module.css';
import * as axios from 'axios';
import userPhoto from '../../assets/images/user.png';

class Users extends React.Component {
	componentDidMount = () => {
		axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${this.props.currentPage}&count=${this.props.pageSize}`)
			.then(response => {
				this.props.setUsers(response.data.items)
			})
	}

	onPageChanged = (pageNumber) => {
		this.props.setCurrentPage(pageNumber)
		axios.get(`https://social-network.samuraijs.com/api/1.0/users?page=${pageNumber}&count=${this.props.pageSize}`)
			.then(response => {
				this.props.setUsers(response.data.items)
			})
	}

	render = () => {

		let pagesCount = Math.ceil(this.props.totalUsersCount / this.props.pageSize)

		let pages = [];

		for (let i = 1; i <= pagesCount; i++) {
			pages.push(i)
		}

		return (
			<div>
				<div>
					{pages.map(p => {
						return <span className={this.props.currentPage === p && s.selected_page}
							onClick={(e) => { this.onPageChanged(p) }}>{p}</span>
					})}
				</div>
				{
					this.props.state.map(u => <div key={u.id} className='user'>
						<div className='user__face'>
							<div className={s.user__avatar}>
								<img src={u.photos.small != null ? u.photos.small : userPhoto} />
							</div>
							<div className='user__button'>
								{u.followed ?
									<button onClick={() => { this.props.unFollow(u.id) }}>unfollow</button> :
									<button onClick={() => { this.props.follow(u.id) }}>follow</button>}
							</div>
						</div>
						<div className='user__block'>
							<div className='user__items'>
								<div className='user__name'>{u.name}</div>
								<div className='user__status'>{u.status}</div>
							</div>
							<div className='user__location'>
								<div className='user__country'>{'u.location.country'}</div>
								<div className='user__city'>{'u.location.city'}</div>
							</div>
						</div>
					</div>)
				}
			</div >
		)
	}
}

export default Users;
