import s from './Users.module.css'
import * as axios from 'axios';

const Users = (props) => {

	if (props.state.length === 0) {
		axios.get('https://social-network.samuraijs.com/api/1.0/users').then(response => {
			debugger
			props.setUsers(response.data.users)
		})
		/* props.setUsers(
			[
				{ id: 1, fullName: 'Alex', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'I\'m fine! Thanks! And you?', location: { city: 'Kyiv', country: 'Ukraine', } },
				{ id: 2, fullName: 'Andrey', followed: false, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'Who is on duty today?', location: { city: 'Washington', country: 'USA', } },
				{ id: 3, fullName: 'Sherlock', followed: false, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'The sun is shining. The weather is fine.', location: { city: 'London', country: 'England', } },
				{ id: 4, fullName: 'Sasha', followed: true, photoUrl: 'https://petapixel.com/assets/uploads/2013/03/The-Iphone-Portrait.jpg', status: 'dast is fantastish, yavol!', location: { city: 'Berlin', country: 'Germany', } },
			]
		) */
	}
	return (
		<div>
			{
				props.state.map(u => <div key={u.id} className='user'>
					<div className='user__face'>
						<div className={s.user__avatar}>
							<img src={u.photoUrl} />
						</div>
						<div className='user__button'>
							{u.followed ?
								<button onClick={() => { props.unFollow(u.id) }}>unfollow</button> :
								<button onClick={() => { props.follow(u.id) }}>follow</button>}
						</div>
					</div>
					<div className='user__block'>
						<div className='user__items'>
							<div className='user__name'>{u.fullName}</div>
							<div className='user__status'>{u.status}</div>
						</div>
						<div className='user__location'>
							<div className='user__country'>{u.location.country}</div>
							<div className='user__city'>{u.location.city}</div>
						</div>
					</div>
				</div>)
			}
		</div >
	)
}

export default Users;
